import React, { useState, useEffect } from 'react';
import './App.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import NewsSquare from './components/NewsSquare';

const App = () => {
  const [news, setNews] = useState([]);

  // Fetch news data from NRK RSS or use dummy data for now
  useEffect(() => {
    fetch('https://api.allorigins.win/get?url=https://www.nrk.no/nyheter/siste.rss')
      .then((response) => response.json())
      .then((data) => {
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(data.contents, 'text/xml');
        const items = xmlDoc.getElementsByTagName('item');

        const newsData = Array.from(items).slice(0, 6).map((item) => ({
          title: item.getElementsByTagName('title')[0]?.textContent || 'No title',
          description: item.getElementsByTagName('description')[0]?.textContent || 'No description',
          image:
            item.getElementsByTagName('media:content')[0]?.getAttribute('url') ||
            'https://via.placeholder.com/300', // Fallback image
        }));

        setNews(newsData);
      })
      .catch((err) => console.error('Error fetching news:', err));
  }, []);

  return (
    <div className="App">
      <div className="column">
        <Swiper direction="vertical" className="swiper">
          {news.slice(0, 3).map((article, index) => (
            <SwiperSlide key={index} className="square">
              <NewsSquare
                title={article.title}
                description={article.description}
                image={article.image}
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
      <div className="column">
        <Swiper direction="vertical" className="swiper">
          {news.slice(3, 6).map((article, index) => (
            <SwiperSlide key={index} className="square">
              <NewsSquare
                title={article.title}
                description={article.description}
                image={article.image}
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

export default App;
