import React, { useState, useEffect } from 'react';
import './App.css';
import NewsSquare from './components/NewsSquare';
import WeatherSquare from './components/WeatherSquare';

const App = () => {
    const [latestNews, setLatestNews] = useState(null);
    const [weather, setWeather] = useState(null);

    // Fetch the latest news
    useEffect(() => {
        fetch('https://api.allorigins.win/get?url=https://www.nrk.no/nyheter/siste.rss')
            .then((response) => response.json())
            .then((data) => {
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(data.contents, 'text/xml');
                const item = xmlDoc.getElementsByTagName('item')[0]; // Only the latest news

                const newsData = {
                    title: item?.getElementsByTagName('title')[0]?.textContent || 'No title',
                    image:
                        item?.getElementsByTagName('media:content')[0]?.getAttribute('url') ||
                        'https://via.placeholder.com/300',
                };

                setLatestNews(newsData);
            })
            .catch((err) => console.error('Error fetching news:', err));
    }, []);

    // Fetch weather data
    useEffect(() => {
        const locationKey = "59.91,10.75"; // Example for Oslo (latitude, longitude)
        const apiKey = "YOUR_API_KEY"; // Replace with your YR API key
        fetch(
            `https://api.met.no/weatherapi/locationforecast/2.0/compact?lat=${locationKey.split(",")[0]}&lon=${locationKey.split(",")[1]}`
        )
            .then((response) => response.json())
            .then((data) => {
                const now = new Date();
                const hour = now.getHours();

                const image =
                    hour < 12
                        ? '/morningphoto.jpg'
                        : hour < 18
                        ? '/middayphoto.jpg'
                        : '/eveningphoto.jpg';

                const temperature = data.properties.timeseries[0].data.instant.details.air_temperature;
                const feelsLike = data.properties.timeseries[0].data.instant.details.relative_humidity;

                setWeather({
                    temperature,
                    feelsLike,
                    image,
                });
            })
            .catch((err) => console.error('Error fetching weather:', err));
    }, []);

    return (
        <div className="App">
            <div className="row">
                <div className="column">
                    {weather && (
                        <WeatherSquare
                            temperature={weather.temperature}
                            feelsLike={weather.feelsLike}
                            image={weather.image}
                        />
                    )}
                </div>
                <div className="column">
                    {latestNews && (
                        <NewsSquare title={latestNews.title} image={latestNews.image} />
                    )}
                </div>
            </div>
        </div>
    );
};

export default App;
