import React, { useState, useEffect } from 'react';
import './NewsSquare.css';
import axios from 'axios';

const NewsSquare = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(
          'https://cors-anywhere.herokuapp.com/https://www.nrk.no/nyheter/siste.rss',
          { headers: { 'X-Requested-With': 'XMLHttpRequest' } }
        );
        const parser = new DOMParser();
        const xml = parser.parseFromString(response.data, 'text/xml');
        const items = Array.from(xml.querySelectorAll('item'))
          .slice(0, 3) // Get only the top 3 news
          .map((item) => ({
            title: item.querySelector('title')?.textContent || '',
            image: item.querySelector('media\\:content')?.getAttribute('url') || '/placeholder.jpg',
          }));
        setNews(items);
      } catch (error) {
        console.error('Error fetching news:', error);
      }
    };
    fetchNews();
  }, []);

  return (
    <div className="news-square">
      {news.map((item, index) => (
        <div key={index} className="news-item">
          <img src={item.image} alt={item.title} className="news-image" />
          <h2 className="news-title">{item.title}</h2>
        </div>
      ))}
    </div>
  );
};

export default NewsSquare;
